import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './ProductManager.css';

const categories = [
  "Motherboards",
  "Processadores",
  "Placas Gráficas",
  "RAM",
  "Discos",
  "Fontes de Alimentação",
  "Caixas",
  "Monitores",
  "Teclados",
  "Ratos"
];

const ProductManager = ({ onAddProduct }) => {
  const [name, setName] = useState('');
  const [category, setCategory] = useState(categories[0]);
  const [price, setPrice] = useState('');
  const [stock, setStock] = useState('');
  const [manufacturer, setManufacturer] = useState('');
  const [description, setDescription] = useState('');
  const [image, setImage] = useState(null);

  const handleAddProduct = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append('name', name);
    formData.append('category', category);
    formData.append('price', price);
    formData.append('stock', stock);
    formData.append('manufacturer', manufacturer);
    formData.append('description', description);
    if (image) {
      formData.append('image', image);
    }

    try {
      const response = await fetch('http://localhost:5000/api/products', {
        method: 'POST',
        body: formData,
      });
      const newProduct = await response.json();
      onAddProduct(newProduct);
      setName('');
      setCategory(categories[0]);
      setPrice('');
      setStock('');
      setManufacturer('');
      setDescription('');
      setImage(null);
    } catch (error) {
      console.error('Erro ao adicionar produto:', error);
    }
  };

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
  };

  return (
    <div className="manage-container">
      <h2>Gerir Produtos</h2>
      <div className="tab-container">
        <Link to="/gerir-produtos" className="tab-link">Voltar à Lista</Link>
      </div>
      <form className="manage-form" onSubmit={handleAddProduct}>
        <label>Nome:</label>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
        <label>Categoria:</label>
        <select value={category} onChange={(e) => setCategory(e.target.value)} required>
          {categories.map((cat) => (
            <option key={cat} value={cat}>{cat}</option>
          ))}
        </select>
        <label>Preço:</label>
        <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} required />
        <label>Stock:</label>
        <input type="number" value={stock} onChange={(e) => setStock(e.target.value)} required />
        <label>Fabricante:</label>
        <input type="text" value={manufacturer} onChange={(e) => setManufacturer(e.target.value)} required />
        <label>Descrição:</label>
        <textarea value={description} onChange={(e) => setDescription(e.target.value)} required></textarea>
        <label>Imagem:</label>
        <input type="file" accept="image/*" onChange={handleImageChange} />
        <button type="submit">Adicionar Produto</button>
      </form>
    </div>
  );
};

export default ProductManager;
