import { useState, useCallback, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';

const useCart = () => {
  const { user } = useAuth();
  const [cart, setCart] = useState([]);

  const fetchCartItems = async () => {
    if (!user) return;

    try {
      const response = await fetch('http://localhost:5000/api/cart', {
        headers: {
          'Content-Type': 'application/json',
          'x-user-email': user.email,
        },
      });
      if (response.ok) {
        const cartData = await response.json();
        setCart(cartData);
      } else {
        console.error('Failed to fetch cart items', response.statusText);
      }
    } catch (error) {
      console.error('Failed to fetch cart items', error);
    }
  };

  useEffect(() => {
    fetchCartItems();
  }, [user]);

  const addToCart = useCallback(async (product) => {
    if (!user) return;

    try {
      const cartItem = { id: product.id, name: product.name, price: product.price, quantity: 1 };
      const response = await fetch('http://localhost:5000/api/cart', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'x-user-email': user.email,
        },
        body: JSON.stringify(cartItem),
      });
      if (response.ok) {
        fetchCartItems();
      } else {
        console.error('Failed to add item to cart', response.statusText);
      }
    } catch (error) {
      console.error('Failed to add item to cart', error);
    }
  }, [user]);

  const removeFromCart = useCallback(async (productId) => {
    if (!user) return;

    try {
      const response = await fetch(`http://localhost:5000/api/cart/${productId}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          'x-user-email': user.email,
        },
      });
      if (response.ok) {
        fetchCartItems();
      } else {
        console.error('Failed to remove item from cart', response.statusText);
      }
    } catch (error) {
      console.error('Failed to remove item from cart', error);
    }
  }, [user]);

  const clearCart = useCallback(async () => {
    if (!user) return;

    try {
      const response = await fetch('http://localhost:5000/api/cart', {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
          'x-user-email': user.email,
        },
      });
      if (response.ok) {
        fetchCartItems();
      } else {
        console.error('Failed to clear cart', response.statusText);
      }
    } catch (error) {
      console.error('Failed to clear cart', error);
    }
  }, [user]);

  const getTotalItems = useCallback(() => {
    return cart.reduce((total, item) => total + item.quantity, 0);
  }, [cart]);

  return { cart, addToCart, removeFromCart, clearCart, getTotalItems };
};

export default useCart;
