import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css'; // Import the carousel styles
import './Home.css';

const Home = () => {
  const [campaigns, setCampaigns] = useState([]);
  const [products, setProducts] = useState([]);
  const [campaignProducts, setCampaignProducts] = useState([]);

  useEffect(() => {
    fetchCampaigns();
    fetchProducts();
    fetchCampaignProducts();
  }, []);

  const fetchCampaigns = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/campaigns');
      const data = await response.json();
      setCampaigns(data);
    } catch (error) {
      console.error('Error fetching campaigns:', error);
    }
  };

  const fetchProducts = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/products');
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const fetchCampaignProducts = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/campaign-products');
      const data = await response.json();
      setCampaignProducts(data);
    } catch (error) {
      console.error('Error fetching campaign products:', error);
    }
  };

  const getProductsForCampaign = (campaignId) => {
    const productIds = campaignProducts
      .filter(cp => cp.campaignId === campaignId)
      .map(cp => cp.productId);
    return products.filter(product => productIds.includes(parseInt(product.id)));
  };

  const calculateDiscountedPrice = (price, discount) => {
    return (price * (1 - discount / 100)).toFixed(2);
  };

  return (
    <div className="home">
      <Navbar />
      <div className="promotions">
        <Carousel autoPlay interval={3000} infiniteLoop showThumbs={false}>
          <div>
            <img src="/images/HomeCarousel/COOLER.jpg" alt="Slide 1" />
          </div>
          <div>
            <img src="/images/HomeCarousel/GPU.webp" alt="Slide 2" />
          </div>
          <div>
            <img src="/images/HomeCarousel/MONITOR.webp" alt="Slide 3" />
          </div>
        </Carousel>
      </div>
      {campaigns.map(campaign => (
        <div key={campaign.id} className="campaign-section">
          <h2>{campaign.name}</h2>
          <div className="product-grid">
            {getProductsForCampaign(campaign.id).map(product => (
              <div key={product.id} className="product-card">
                <img src={product.image} alt={product.name} className="product-image" />
                <h3>{product.name}</h3>
                <p>{product.manufacturer}</p>
                <p>{product.description}</p>
                <p>
                  {calculateDiscountedPrice(parseFloat(product.price), parseFloat(campaign.discount))}€
                  <span className="original-price">{parseFloat(product.price)}€</span>
                </p>
                <p>Stock: {product.stock}</p>
              </div>
            ))}
          </div>
        </div>
      ))}
      <Footer />
    </div>
  );
};

export default Home;
