import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css'; // Import the carousel styles
import NewsSection from '../components/NewsSection';  // Importe o novo componente
import HighlightsSection from '../components/HighlightsSection';  // Importe o novo componente
import './Home.css';

const Home = () => {
  const [products, setProducts] = useState([]);
  const [promotions, setPromotions] = useState({ news: [], highlights: [] });

  useEffect(() => {
    fetchNewArrivals();
    fetchFeatured();
    fetchPromotions();
  }, []);

  const fetchNewArrivals = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/products/new-arrivals');
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      console.error('Error fetching new arrivals:', error);
    }
  };

  const fetchFeatured = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/products/featured');
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      console.error('Error fetching featured products:', error);
    }
  };

  const fetchPromotions = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/promotions');
      const data = await response.json();
      setPromotions(data);
    } catch (error) {
      console.error('Error fetching promotions:', error);
    }
  };

  return (
    <div className="home">
      <Navbar />
      <div className="promotions">
        <Carousel autoPlay interval={3000} infiniteLoop showThumbs={false}>
          <div>
            <img src="/images/HomeCarousel/COOLER.jpg" alt="Slide 1" />
          </div>
          <div>
            <img src="/images/HomeCarousel/GPU.webp" alt="Slide 2" />
          </div>
          <div>
            <img src="/images/HomeCarousel/MONITOR.webp" alt="Slide 3" />
          </div>
        </Carousel>
      </div>
      <NewsSection products={products.filter(product => promotions.news.includes(product.id))} />
      <HighlightsSection products={products.filter(product => promotions.highlights.includes(product.id))} />
      <Footer />
    </div>
  );
};

export default Home;
