import React, { useState } from 'react';
import './ManagePromotions.css';

const ManagePromotions = ({ products, promotionType, onAdd, onEdit, onDelete }) => {
  const [selectedProduct, setSelectedProduct] = useState('');
  const [editingProduct, setEditingProduct] = useState(null);

  const handleAddPromotion = async () => {
    if (selectedProduct) {
      try {
        const response = await fetch('http://localhost:5000/api/promotions', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ id: parseInt(selectedProduct), type: promotionType }),
        });
        if (response.ok) {
          onAdd(parseInt(selectedProduct), promotionType);
          setSelectedProduct('');
        } else {
          console.error('Failed to add promotion');
        }
      } catch (error) {
        console.error('Error adding promotion:', error);
      }
    }
  };

  const handleDeletePromotion = async (productId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/promotions/${productId}/${promotionType}`, {
        method: 'DELETE',
      });
      if (response.ok) {
        onDelete(productId);
      } else {
        console.error('Failed to delete promotion');
      }
    } catch (error) {
      console.error('Error deleting promotion:', error);
    }
  };

  const handleEditSave = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/products/${editingProduct.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(editingProduct),
      });
      if (response.ok) {
        onEdit(editingProduct);
        setEditingProduct(null);
      } else {
        console.error('Failed to update product');
      }
    } catch (error) {
      console.error('Error updating product:', error);
    }
  };

  return (
    <div className="manage-promotions-container">
      <h2>Gerir {promotionType === 'news' ? 'Novidades' : 'Destaques'}</h2>
      <div className="add-section">
        <select onChange={(e) => setSelectedProduct(e.target.value)} value={selectedProduct}>
          <option value="">Selecione um Produto</option>
          {products.map((product) => (
            <option key={product.id} value={product.id}>
              {product.name}
            </option>
          ))}
        </select>
        <button onClick={handleAddPromotion}>Adicionar {promotionType === 'news' ? 'Novidade' : 'Destaque'}</button>
      </div>
      <div className="promotion-table">
        <table>
          <thead>
            <tr>
              <th>Nome</th>
              <th>Categoria</th>
              <th>Fabricante</th>
              <th>Preço</th>
              <th>Stock</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {products.map(product => (
              <tr key={product.id}>
                <td>{product.name}</td>
                <td>{product.category}</td>
                <td>{product.manufacturer}</td>
                <td>{product.price}€</td>
                <td>{product.stock}</td>
                <td>
                  <button onClick={() => setEditingProduct(product)}>Editar</button>
                  <button onClick={() => handleDeletePromotion(product.id)}>Remover</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {editingProduct && (
        <div className="edit-section">
          <h3>Editar Produto</h3>
          <input
            type="text"
            value={editingProduct.name}
            onChange={(e) => setEditingProduct({ ...editingProduct, name: e.target.value })}
            placeholder="Nome"
          />
          <input
            type="text"
            value={editingProduct.category}
            onChange={(e) => setEditingProduct({ ...editingProduct, category: e.target.value })}
            placeholder="Categoria"
          />
          <input
            type="text"
            value={editingProduct.manufacturer}
            onChange={(e) => setEditingProduct({ ...editingProduct, manufacturer: e.target.value })}
            placeholder="Fabricante"
          />
          <input
            type="number"
            value={editingProduct.price}
            onChange={(e) => setEditingProduct({ ...editingProduct, price: e.target.value })}
            placeholder="Preço"
          />
          <input
            type="number"
            value={editingProduct.stock}
            onChange={(e) => setEditingProduct({ ...editingProduct, stock: e.target.value })}
            placeholder="Stock"
          />
          <button onClick={handleEditSave}>Salvar</button>
        </div>
      )}
    </div>
  );
};

export default ManagePromotions;
