import React from 'react';
import { Link } from 'react-router-dom';
import './CampaignManager.css';

const CampaignManager = ({ campaigns, onAddCampaign, onDeleteCampaign }) => {
  const handleDelete = (id) => {
    onDeleteCampaign(id);
  };

  return (
    <div className="campaign-container">
      <h2>Gerir Campanhas</h2>
      <button className="btn-add-campaign"><Link to="/adicionar-campanha">Adicionar Campanha</Link></button>
      <table className="campaign-table">
        <thead>
          <tr>
            <th>Nome</th>
            <th>Desconto</th>
            <th>Data de Início</th>
            <th>Data de Fim</th>
            <th>Ativa</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {campaigns.map(campaign => (
            <tr key={campaign.id}>
              <td>{campaign.name}</td>
              <td>{campaign.discount}%</td>
              <td>{campaign.startDate}</td>
              <td>{campaign.endDate}</td>
              <td>{campaign.active ? 'Sim' : 'Não'}</td>
              <td>
                <Link to={`/editar-campanha/${campaign.id}`}>
                  <button className="btn-edit-campaign">Editar</button>
                </Link>
                <button className="btn-edit-campaign" onClick={() => handleDelete(campaign.id)}>Remover</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CampaignManager;
