import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './CampaignManager.css';

const CampaignManager = ({ campaigns, onAddCampaign, onDeleteCampaign }) => {
  const [newCampaign, setNewCampaign] = useState({
    name: '',
    discount: '',
    startDate: '',
    endDate: '',
    active: false
  });
  const [showForm, setShowForm] = useState(false);

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setNewCampaign((prevCampaign) => ({
      ...prevCampaign,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onAddCampaign(newCampaign);
    setNewCampaign({
      name: '',
      discount: '',
      startDate: '',
      endDate: '',
      active: false
    });
    setShowForm(false);
  };

  return (
    <div className="campaign-manager">
      {!showForm && (
        <button className="btn-add-campaign" onClick={() => setShowForm(true)}>
          Adicionar Campanha
        </button>
      )}
      {showForm && (
        <form className="campaign-form" onSubmit={handleSubmit}>
          <label>Nome:</label>
          <input type="text" name="name" value={newCampaign.name} onChange={handleChange} required />
          <label>Desconto (%):</label>
          <input type="number" name="discount" value={newCampaign.discount} onChange={handleChange} required />
          <label>Data de InÃ­cio:</label>
          <input type="date" name="startDate" value={newCampaign.startDate} onChange={handleChange} required />
          <label>Data de Fim:</label>
          <input type="date" name="endDate" value={newCampaign.endDate} onChange={handleChange} required />
          <label>Ativa:</label>
          <input type="checkbox" name="active" checked={newCampaign.active} onChange={handleChange} className="checkbox-active" />
          <button type="submit" className="btn-submit-campaign">Adicionar Campanha</button>
        </form>
      )}
      <table className="campaign-table">
        <thead>
          <tr>
            <th>Nome</th>
            <th>Desconto</th>
            <th>Data de InÃ­cio</th>
            <th>Data de Fim</th>
            <th>Ativa</th>
            <th>AÃ§Ãµes</th>
          </tr>
        </thead>
        <tbody>
          {campaigns.map((campaign) => (
            <tr key={campaign.id}>
              <td>{campaign.name}</td>
              <td>{campaign.discount}%</td>
              <td>{campaign.startDate}</td>
              <td>{campaign.endDate}</td>
              <td>{campaign.active ? 'Sim' : 'NÃ£o'}</td>
              <td>
                <Link to={`/editar-campanha/${campaign.id}`}>
                  <button className="btn-edit-campaign">Editar</button>
                </Link>
                <button className="btn-delete-campaign" onClick={() => onDeleteCampaign(campaign.id)}>Remover</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default CampaignManager;
