import React, { useState } from 'react';
import './Contactos.css';
import fs from 'fs'; // Importa o módulo fs para lidar com operações de arquivo

const Contactos = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    message: ''
  });

  // Função para salvar os dados do formulário em um arquivo JSON
  const saveFormDataToJson = (formData) => {
    // Caminho onde o arquivo JSON será salvo
    const filePath = 'server/data/Contactos.json';

    // Converte o objeto formData para JSON
    const jsonData = JSON.stringify(formData, null, 2); // O segundo argumento null e o terceiro argumento 2 são para formatação

    // Escreve os dados JSON no arquivo
    fs.writeFile(filePath, jsonData, (err) => {
      if (err) {
        console.error('Erro ao salvar os dados do formulário:', err);
        return;
      }
      console.log('Dados do formulário foram salvos com sucesso em', filePath);
    });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Chamada da função para salvar os dados do formulário em um arquivo JSON
    saveFormDataToJson(formData);

    // Aqui você pode adicionar a lógica para enviar os dados do formulário
    console.log(formData);

    // Após enviar o formulário, você pode limpar os campos
    setFormData({
      name: '',
      email: '',
      message: ''
    });
  };

  return (
    <div className="contact-container">
      <h1 className="contact-title">Entre em Contato</h1>
      <form className="contact-form" onSubmit={handleSubmit}>
        <label htmlFor="name">Nome:</label>
        <input
          type="text"
          id="name"
          name="name"
          value={formData.name}
          onChange={handleChange}
          required
        />
        <label htmlFor="email">Email:</label>
        <input
          type="email"
          id="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <label htmlFor="message">Mensagem:</label>
        <textarea
          id="message"
          name="message"
          value={formData.message}
          onChange={handleChange}
          required
        ></textarea>
        <button type="submit" className="submit-button">Enviar</button>
      </form>
    </div>
  );
};

export default Contactos;
