import React, { useState } from 'react';
import './ProductManager.css';

const ProductManager = ({ onAddProduct }) => {
  const [name, setName] = useState('');
  const [category, setCategory] = useState('');
  const [manufacturer, setManufacturer] = useState('');
  const [price, setPrice] = useState('');
  const [stock, setStock] = useState('');
  const [description, setDescription] = useState('');
  const [image, setImage] = useState(null);

  const handleAddProduct = (e) => {
    e.preventDefault();
    const newProduct = {
      name,
      category,
      manufacturer,
      price: parseFloat(price),
      stock: parseInt(stock),
      description,
      image,
    };
    onAddProduct(newProduct);
    setName('');
    setCategory('');
    setManufacturer('');
    setPrice('');
    setStock('');
    setDescription('');
    setImage(null);
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(file);
    }
  };

  return (
    <div className="manage-container">
      <h2>Adicionar Produto</h2>
      <form className="manage-form" onSubmit={handleAddProduct}>
        <label>Nome:</label>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
        
        <label>Categoria:</label>
        <input type="text" value={category} onChange={(e) => setCategory(e.target.value)} required />
        
        <label>Fabricante:</label>
        <input type="text" value={manufacturer} onChange={(e) => setManufacturer(e.target.value)} required />
        
        <label>Preço:</label>
        <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} required />
        
        <label>Stock:</label>
        <input type="number" value={stock} onChange={(e) => setStock(e.target.value)} required />
        
        <label>Descrição:</label>
        <textarea value={description} onChange={(e) => setDescription(e.target.value)} required></textarea>
        
        <label>Imagem:</label>
        <input type="file" accept="image/*" onChange={handleImageChange} />
        {image && <img src={URL.createObjectURL(image)} alt="Product" className="preview-image" />}
        
        <button type="submit">Adicionar Produto</button>
      </form>
    </div>
  );
};

export default ProductManager;
