import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useTheme } from '../context/ThemeContext'; // Importe o contexto do tema
import CategoryMenu from './CategoryMenu';
import './Navbar.css';

const Navbar = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [isAnimating, setIsAnimating] = useState(false);
  const { isDarkMode, toggleDarkMode } = useTheme(); // Use o estado global do tema

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const handleButtonClick = () => {
    setIsAnimating(!isAnimating);
  };

  return (
    <nav className={`navbar ${isDarkMode ? 'dark-mode' : ''}`}>
      <CategoryMenu />
      <div className="navbar-brand">
        <Link to="/" className="navbar-logo">TechFlow</Link>
      </div>
      <ul className="navbar-links">
        <li><Link to="/configurator">Configurador</Link></li>
        <li><Link to="/">Campanhas e Ofertas</Link></li>
        {user && user.role === 'gestor' && (
          <>
            <li><Link to="/gerir-produtos">Gerir Produtos</Link></li>
            <li><Link to="/gerir-campanhas">Gerir Campanhas</Link></li>
            <li><Link to="/gerir-pedidos">Gerir Pedidos</Link></li>
          </>
        )}
      </ul>
      <div className="navbar-search">
        <input type="text" placeholder="Search..." />
        {/* Elemento da bolinha deslizante */}
        <div className={`slider ${isAnimating ? 'slide' : ''}`}></div>
      </div>
      <div className="navbar-icons">
        <button onClick={toggleDarkMode}>
          {isDarkMode ? 'Modo Claro' : 'Modo Escuro'}
        </button>
        {user ? (
          <>
            <Link to="/edit-profile" className="email-link">{user.email}</Link>
            <button onClick={handleLogout}>Logout</button>
          </>
        ) : (
          <Link to="/login">ðŸ‘¤</Link>
        )}
        <span className="cart-icon">ðŸ›’(0)</span>
      </div>
    </nav>
  );
};

export default Navbar;
