import React, { useState, useEffect } from 'react';
import './CampaignManager.css';

const CampaignManager = () => {
  const [products, setProducts] = useState([]);
  const [promotions, setPromotions] = useState({ news: [], highlights: [] });
  const [selectedProduct, setSelectedProduct] = useState('');
  const [campaignType, setCampaignType] = useState('news'); // Default to 'news'
  const [editingProduct, setEditingProduct] = useState(null);

  useEffect(() => {
    fetchProducts();
    fetchPromotions();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/products');
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const fetchPromotions = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/promotions');
      const data = await response.json();
      setPromotions(data);
    } catch (error) {
      console.error('Error fetching promotions:', error);
    }
  };

  const handleAddPromotion = async () => {
    if (!selectedProduct) return;

    try {
      const response = await fetch('http://localhost:5000/api/promotions', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ type: campaignType, productId: selectedProduct })
      });
      const data = await response.json();
      setPromotions(data);
      setSelectedProduct(''); // Clear the selection after adding
    } catch (error) {
      console.error('Error adding promotion:', error);
    }
  };

  const handleRemovePromotion = async (productId) => {
    console.log('Attempting to remove product with ID:', productId); // Log do ID do produto a ser removido
    try {
      const response = await fetch('http://localhost:5000/api/promotions', {
        method: 'DELETE',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ type: campaignType, productId: productId.toString() })
      });
      const data = await response.json();
      console.log('Promotions after delete:', data); // Log da resposta da requisição
      setPromotions(data);
    } catch (error) {
      console.error('Error removing promotion:', error);
    }
  };

  const handleEditProduct = (product) => {
    setEditingProduct(product);
  };

  const handleSaveEdit = async () => {
    try {
      const response = await fetch(`http://localhost:5000/api/products/${editingProduct.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(editingProduct)
      });
      const data = await response.json();
      setProducts(products.map(p => (p.id === editingProduct.id ? editingProduct : p)));
      setEditingProduct(null);
    } catch (error) {
      console.error('Error updating product:', error);
    }
  };

  return (
    <div className="campaign-manager">
      <h2>Gerir Campanhas</h2>
      <div className="campaign-buttons">
        <button onClick={() => setCampaignType('news')} className={campaignType === 'news' ? 'active' : ''}>Gerir Novidades</button>
        <button onClick={() => setCampaignType('highlights')} className={campaignType === 'highlights' ? 'active' : ''}>Gerir Destaques</button>
      </div>
      <div className="campaign-content">
        <h3>{campaignType === 'news' ? 'Gerir Novidades' : 'Gerir Destaques'}</h3>
        <select value={selectedProduct} onChange={(e) => setSelectedProduct(e.target.value)}>
          <option value="">Selecione um Produto</option>
          {products.map(product => (
            <option key={product.id} value={product.id}>{product.name}</option>
          ))}
        </select>
        <button onClick={handleAddPromotion} className="add-promotion-btn">Adicionar {campaignType === 'news' ? 'Novidade' : 'Destaque'}</button>
        <table className="campaign-table">
          <thead>
            <tr>
              <th>Nome</th>
              <th>Categoria</th>
              <th>Fabricante</th>
              <th>Preço</th>
              <th>Stock</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {promotions[campaignType].map(productId => {
              const product = products.find(p => p.id === parseInt(productId));
              return product ? (
                <tr key={product.id}>
                  <td>{product.name}</td>
                  <td>{product.category}</td>
                  <td>{product.manufacturer}</td>
                  <td>{product.price}€</td>
                  <td>{product.stock}</td>
                  <td>
                    <button onClick={() => handleEditProduct(product)} className="edit-btn">Editar</button>
                    <button onClick={() => handleRemovePromotion(product.id)} className="remove-btn">Remover</button>
                  </td>
                </tr>
              ) : null;
            })}
          </tbody>
        </table>
        {editingProduct && (
          <div className="edit-product-form">
            <h3>Editar Produto</h3>
            <label>
              Nome:
              <input
                type="text"
                value={editingProduct.name}
                onChange={(e) => setEditingProduct({ ...editingProduct, name: e.target.value })}
              />
            </label>
            <label>
              Categoria:
              <input
                type="text"
                value={editingProduct.category}
                onChange={(e) => setEditingProduct({ ...editingProduct, category: e.target.value })}
              />
            </label>
            <label>
              Fabricante:
              <input
                type="text"
                value={editingProduct.manufacturer}
                onChange={(e) => setEditingProduct({ ...editingProduct, manufacturer: e.target.value })}
              />
            </label>
            <label>
              Preço:
              <input
                type="number"
                value={editingProduct.price}
                onChange={(e) => setEditingProduct({ ...editingProduct, price: e.target.value })}
              />
            </label>
            <label>
              Stock:
              <input
                type="number"
                value={editingProduct.stock}
                onChange={(e) => setEditingProduct({ ...editingProduct, stock: e.target.value })}
              />
            </label>
            <button onClick={handleSaveEdit} className="save-btn">Salvar</button>
            <button onClick={() => setEditingProduct(null)} className="cancel-btn">Cancelar</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default CampaignManager;
