import React, { useState } from 'react';
import './ManagePromotions.css';

const ManagePromotions = ({ products, onTogglePromotion }) => {
  const [selectedProduct, setSelectedProduct] = useState('');
  const [promotionType, setPromotionType] = useState('news');

  const handleAddPromotion = () => {
    if (selectedProduct && promotionType) {
      onTogglePromotion(selectedProduct, promotionType);
      setSelectedProduct('');
    }
  };

  return (
    <div className="manage-promotions-container">
      <h2>Gerir Promoções</h2>
      <select onChange={(e) => setSelectedProduct(e.target.value)} value={selectedProduct}>
        <option value="">Selecione um Produto</option>
        {products.map((product) => (
          <option key={product.id} value={product.id}>
            {product.name}
          </option>
        ))}
      </select>
      <select onChange={(e) => setPromotionType(e.target.value)} value={promotionType}>
        <option value="news">Novidade</option>
        <option value="highlight">Destaque</option>
      </select>
      <button onClick={handleAddPromotion}>Adicionar Promoção</button>
    </div>
  );
};

export default ManagePromotions;
