import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import './EditProfile.css';

const EditProfile = () => {
  const { user } = useAuth();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    nif: '',
    address: '',
    postalCode: '',
    city: '',
    phone: '',
    password: '',
    confirmPassword: ''
  });

  useEffect(() => {
    if (user) {
      setFormData({
        name: user.name,
        email: user.email,
        nif: user.nif || '',
        address: user.address || '',
        postalCode: user.postalCode || '',
        city: user.city || '',
        phone: user.phone || '',
        password: '',
        confirmPassword: ''
      });
    }
  }, [user]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Adicione a lógica de envio para atualizar os dados do usuário
  };

  return (
    <div className="edit-profile-container">
      <h2>Editar Perfil</h2>
      <form onSubmit={handleSubmit}>
        <label>Nome:</label>
        <input type="text" name="name" value={formData.name} onChange={handleChange} required />
        <label>Email:</label>
        <input type="email" name="email" value={formData.email} onChange={handleChange} required readOnly />
        <label>NIF:</label>
        <input type="text" name="nif" value={formData.nif} onChange={handleChange} required />
        <label>Endereço:</label>
        <input type="text" name="address" value={formData.address} onChange={handleChange} required />
        <label>Código Postal:</label>
        <input type="text" name="postalCode" value={formData.postalCode} onChange={handleChange} required />
        <label>Cidade:</label>
        <input type="text" name="city" value={formData.city} onChange={handleChange} required />
        <label>Telemóvel:</label>
        <input type="text" name="phone" value={formData.phone} onChange={handleChange} required />
        <button type="submit">Atualizar Perfil</button>
      </form>
    </div>
  );
};

export default EditProfile;
