// Faq.js

import React, { useState } from 'react';
import './Faq.css';

const Faq = () => {
  const [activeIndex, setActiveIndex] = useState(null);

  const toggleAnswer = (index) => {
    setActiveIndex(activeIndex === index ? null : index);
  };

  return (
    <div className="faq-container">
      <h2 className="faq-title">Perguntas Frequentes</h2>
      <div className="faq-items">
        <div className="faq-item" onClick={() => toggleAnswer(0)}>
          <h3 className="question">Como faço para realizar um pedido?</h3>
          {activeIndex === 0 && (
            <p className="answer">Para realizar um pedido, basta navegar até a página de produtos, escolher o produto desejado e seguir as instruções de checkout.</p>
          )}
        </div>
        <div className="faq-item" onClick={() => toggleAnswer(1)}>
          <h3 className="question">Qual é o prazo de entrega dos produtos?</h3>
          {activeIndex === 1 && (
            <p className="answer">O prazo de entrega pode variar dependendo da localização e do método de envio escolhido. Geralmente, os produtos são entregues dentro de 5 a 7 dias úteis.</p>
          )}
        </div>
        {/* Adicione mais perguntas e respostas conforme necessário */}
      </div>
    </div>
  );
};

export default Faq;
