import React, { useState, useEffect } from 'react';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import { Carousel, Card, Container, Row, Col } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Home.css';

const Home = () => {
  const [campaigns, setCampaigns] = useState([]);
  const [campaignProducts, setCampaignProducts] = useState({});
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetchCampaigns();
    fetchProducts();
  }, []);

  useEffect(() => {
    if (campaigns.length > 0) {
      campaigns.forEach(campaign => {
        fetchCampaignProducts(campaign.id);
      });
    }
  }, [campaigns]);

  const fetchCampaigns = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/campaigns');
      const data = await response.json();
      const activeCampaigns = data.filter(campaign => campaign.active);
      setCampaigns(activeCampaigns);
    } catch (error) {
      console.error('Error fetching campaigns:', error);
    }
  };

  const fetchCampaignProducts = async (campaignId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/campaign-products/${campaignId}`);
      const data = await response.json();
      setCampaignProducts(prevState => ({
        ...prevState,
        [campaignId]: data
      }));
    } catch (error) {
      console.error(`Error fetching products for campaign ${campaignId}:`, error);
    }
  };

  const fetchProducts = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/products');
      const data = await response.json();
      setProducts(data);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const calculateDiscountedPrice = (price, discount) => {
    return (price - (price * discount / 100)).toFixed(2);
  };

  return (
    <div className="home">
      <Navbar />
      <div className="promotions">
        <Carousel autoPlay interval={3000} infiniteLoop showThumbs={false}>
          <Carousel.Item>
            <img src="/images/HomeCarousel/COOLER.jpg" alt="Slide 1" className="d-block w-100" />
          </Carousel.Item>
          <Carousel.Item>
            <img src="/images/HomeCarousel/GPU.webp" alt="Slide 2" className="d-block w-100" />
          </Carousel.Item>
          <Carousel.Item>
            <img src="/images/HomeCarousel/MONITOR.webp" alt="Slide 3" className="d-block w-100" />
          </Carousel.Item>
        </Carousel>
      </div>
      {campaigns.map(campaign => (
        <Container key={campaign.id} className="campaign-container mb-4 p-3">
          <h2>{campaign.name}</h2>
          <Carousel interval={null}>
            {campaignProducts[campaign.id] && campaignProducts[campaign.id].map((product, index) => (
              <Carousel.Item key={product.id}>
                <Row className="justify-content-center">
                  <Col xs={12} md={6} lg={3}>
                    <Card className="product-card">
                      <Card.Img variant="top" src={product.image} />
                      <Card.Body>
                        <Card.Title>{product.name}</Card.Title>
                        <Card.Text>
                          {product.manufacturer}
                          <br />
                          {product.description}
                          <br />
                          Preço Original: {product.price}€
                          <br />
                          Preço com Desconto: {calculateDiscountedPrice(product.price, campaign.discount)}€
                          <br />
                          Stock: {product.stock}
                        </Card.Text>
                      </Card.Body>
                    </Card>
                  </Col>
                </Row>
              </Carousel.Item>
            ))}
          </Carousel>
        </Container>
      ))}
      <Footer />
    </div>
  );
};

export default Home;
