import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext'; // Ajuste o caminho conforme a estrutura de pastas
import UserProfile from '../components/UserProfile'; // Certifique-se de ajustar o caminho conforme necessário
import './EditProfile.css';

const EditProfile = ({ onUpdateProfile }) => {
  const { user } = useAuth(); // Obtém o usuário do AuthContext
  const [profileData, setProfileData] = useState(null);

  useEffect(() => {
    if (profileData) {
      setFormData({
        name: profileData.name || '',
        email: profileData.email,
        nif: profileData.nif || '',
        address: profileData.address || '',
        postalCode: profileData.postalCode || '',
        city: profileData.city || '',
        phone: profileData.phone || '',
        password: '',
        confirmPassword: ''
      });
    }
  }, [profileData]);

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    nif: '',
    address: '',
    postalCode: '',
    city: '',
    phone: '',
    password: '',
    confirmPassword: ''
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (typeof onUpdateProfile === 'function') {
      onUpdateProfile(formData);
    } else {
      console.error('onUpdateProfile is not a function');
    }
  };

  return (
    <div className="edit-profile-container">
      <h2>Editar Perfil</h2>
      <UserProfile setUserProfile={setProfileData} /> {/* Componente para buscar dados do usuário */}
      <form className="edit-profile-form" onSubmit={handleSubmit}>
        <label>Nome:</label>
        <input type="text" name="name" value={formData.name} onChange={handleChange} required />
        <label>Email:</label>
        <input type="email" name="email" value={formData.email} onChange={handleChange} required readOnly />
        <label>NIF:</label>
        <input type="text" name="nif" value={formData.nif} onChange={handleChange} required />
        <label>Endereço:</label>
        <input type="text" name="address" value={formData.address} onChange={handleChange} required />
        <label>Código Postal:</label>
        <input type="text" name="postalCode" value={formData.postalCode} onChange={handleChange} required />
        <label>Cidade:</label>
        <input type="text" name="city" value={formData.city} onChange={handleChange} required />
        <label>Telemóvel:</label>
        <input type="text" name="phone" value={formData.phone} onChange={handleChange} required />
        <button type="submit">Atualizar Perfil</button>
      </form>
    </div>
  );
};

export default EditProfile;
