import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import './CampaignProducts.css';

const CampaignProducts = ({ products }) => {
  const { campaignId } = useParams();
  const [campaignProducts, setCampaignProducts] = useState([]);
  const [availableProducts, setAvailableProducts] = useState([]);

  useEffect(() => {
    fetch(`http://localhost:5000/api/campaign-products/${campaignId}`)
      .then(response => response.json())
      .then(data => setCampaignProducts(data))
      .catch(error => console.error('Error fetching campaign products:', error));
  }, [campaignId]);

  useEffect(() => {
    setAvailableProducts(products.filter(p => !campaignProducts.some(cp => cp.id === p.id)));
  }, [products, campaignProducts]);

  const handleAddProductToCampaign = (productId) => {
    fetch(`http://localhost:5000/api/campaign-products/${campaignId}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ productId }),
    })
    .then(response => response.json())
    .then(() => setCampaignProducts([...campaignProducts, products.find(p => p.id === productId)]))
    .catch(error => console.error('Error adding product to campaign:', error));
  };

  const handleRemoveProductFromCampaign = (productId) => {
    fetch(`http://localhost:5000/api/campaign-products/${campaignId}/${productId}`, {
      method: 'DELETE',
    })
    .then(() => setCampaignProducts(campaignProducts.filter(cp => cp.id !== productId)))
    .catch(error => console.error('Error removing product from campaign:', error));
  };

  return (
    <div className="manage-campaign-products-container">
      <h2>Gerir Produtos da Campanha</h2>
      <div className="available-products">
        <h3>Produtos Disponíveis</h3>
        <ul>
          {availableProducts.map(product => (
            <li key={product.id}>
              {product.name}
              <button onClick={() => handleAddProductToCampaign(product.id)}>Adicionar</button>
            </li>
          ))}
        </ul>
      </div>
      <div className="campaign-products">
        <h3>Produtos na Campanha</h3>
        <ul>
          {campaignProducts.map(product => (
            <li key={product.id}>
              {product.name}
              <button onClick={() => handleRemoveProductFromCampaign(product.id)}>Remover</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default CampaignProducts;
