import React, { useState } from 'react';
import './ManagePromotions.css';

const ManagePromotions = ({ products, promotionType, onAdd, onEdit, onDelete }) => {
  const [selectedProduct, setSelectedProduct] = useState('');
  const [editingProduct, setEditingProduct] = useState(null);

  const handleAddPromotion = () => {
    if (selectedProduct) {
      onAdd(selectedProduct, promotionType);
      setSelectedProduct('');
    }
  };

  const handleEditPromotion = (product) => {
    setEditingProduct(product);
  };

  const handleSaveEdit = () => {
    onEdit(editingProduct);
    setEditingProduct(null);
  };

  return (
    <div className="manage-promotions-container">
      <h2>Gerir {promotionType === 'news' ? 'Novidades' : 'Destaques'}</h2>
      <select onChange={(e) => setSelectedProduct(e.target.value)} value={selectedProduct}>
        <option value="">Selecione um Produto</option>
        {products.map((product) => (
          <option key={product.id} value={product.id}>
            {product.name}
          </option>
        ))}
      </select>
      <button onClick={handleAddPromotion}>Adicionar {promotionType === 'news' ? 'Novidade' : 'Destaque'}</button>
      {editingProduct && (
        <div className="edit-section">
          <input
            type="text"
            value={editingProduct.name}
            onChange={(e) => setEditingProduct({ ...editingProduct, name: e.target.value })}
          />
          <button onClick={handleSaveEdit}>Salvar</button>
        </div>
      )}
      <div className="promotion-list">
        {products.filter(product => product.promotionType === promotionType).map(product => (
          <div key={product.id} className="promotion-item">
            <span>{product.name}</span>
            <button onClick={() => handleEditPromotion(product)}>Editar</button>
            <button onClick={() => onDelete(product.id)}>Remover</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ManagePromotions;
