// Faq.js

import React, { useState } from 'react';
import './Faq.css';

const Faq = () => {
  const [activeIndex, setActiveIndex] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  const toggleAnswer = (index) => {
    setActiveIndex(activeIndex === index ? null : index);
  };

  const filteredQuestions = () => {
    return FAQ_DATA.filter((item) =>
      item.question.toLowerCase().includes(searchTerm.toLowerCase())
    );
  };

  const randomQuestions = () => {
    const randomIndexes = [];
    while (randomIndexes.length < 10) {
      const randomIndex = Math.floor(Math.random() * FAQ_DATA.length);
      if (!randomIndexes.includes(randomIndex)) {
        randomIndexes.push(randomIndex);
      }
    }
    return randomIndexes.map((index) => FAQ_DATA[index]);
  };

  return (
    <div className="faq-container">
      <h1 className="faq-title">FAQs (Perguntas frequentes)</h1>
      <div className="search-bar">
        <input
          type="text"
          placeholder="Pesquisar..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="faq-search"
        />
        <i className="fa fa-search search-icon"></i>
      </div>
      <div className="faq-items">
        {randomQuestions().map((item, index) => (
          <div className="faq-item" onClick={() => toggleAnswer(index)} key={index}>
            <h2 className="question">{item.question}</h2>
            {activeIndex === index && <p className="answer">{item.answer}</p>}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Faq;

const FAQ_DATA = [
  {
    question: 'Como faço para realizar um pedido?',
    answer: 'Para realizar um pedido, basta navegar até a página de produtos, escolher o produto desejado e seguir as instruções de checkout.',
  },
  {
    question: 'Qual é o prazo de entrega dos produtos?',
    answer: 'O prazo de entrega pode variar dependendo da localização e do método de envio escolhido. Geralmente, os produtos são entregues dentro de 5 a 7 dias úteis.',
  },
  // Adicione mais perguntas e respostas conforme necessário
];
