import React, { useState, useEffect } from 'react';
import './CampaignManager.css';
import ManagePromotions from '../components/ManagePromotions';

const CampaignManager = () => {
  const [view, setView] = useState('');
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/api/products')
      .then(response => response.json())
      .then(data => setProducts(data))
      .catch(error => console.error('Error fetching products:', error));
  }, []);

  const handleAddPromotion = (productId, promotionType) => {
    const updatedProducts = products.map(product => {
      if (product.id === parseInt(productId)) {
        return { ...product, promotionType: promotionType };
      }
      return product;
    });
    setProducts(updatedProducts);
    // Atualizar o backend com a nova promoção
  };

  const handleEditPromotion = (updatedProduct) => {
    const updatedProducts = products.map(product =>
      product.id === updatedProduct.id ? updatedProduct : product
    );
    setProducts(updatedProducts);
    // Atualizar o backend com o produto editado
  };

  const handleDeletePromotion = (productId) => {
    const updatedProducts = products.map(product => {
      if (product.id === productId) {
        return { ...product, promotionType: null };
      }
      return product;
    });
    setProducts(updatedProducts);
    // Atualizar o backend para remover a promoção
  };

  return (
    <div className="manage-container">
      <h2>Gerir Campanhas</h2>
      {!view && (
        <div className="manage-buttons">
          <button onClick={() => setView('news')}>Gerir Novidades</button>
          <button onClick={() => setView('highlights')}>Gerir Destaques</button>
        </div>
      )}
      {view === 'news' && (
        <ManagePromotions
          products={products}
          promotionType="news"
          onAdd={handleAddPromotion}
          onEdit={handleEditPromotion}
          onDelete={handleDeletePromotion}
        />
      )}
      {view === 'highlights' && (
        <ManagePromotions
          products={products}
          promotionType="highlight"
          onAdd={handleAddPromotion}
          onEdit={handleEditPromotion}
          onDelete={handleDeletePromotion}
        />
      )}
    </div>
  );
};

export default CampaignManager;
