import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext'; // Certifique-se de ajustar o caminho conforme necessário

const UserProfile = ({ onUserProfileFetched }) => {
  const { user } = useAuth(); // Obtém o usuário do AuthContext
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        // LINHAS QUE FAZEM O FETCH DOS DADOS 
        const response = await fetch(`/api/user/${user.email}`);
        if (!response.ok) {
          throw new Error('Erro ao buscar dados do usuário');
        }
        const data = await response.json();
        onUserProfileFetched(data); // Passa os dados obtidos para a função callback
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    if (user && user.email) {
      fetchProfileData(); // Chama a função para fazer o fetch dos dados
    }
  }, [user, onUserProfileFetched]);

  if (loading) {
    return <div>Carregando...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return null; // Este componente não precisa renderizar nada, apenas busca os dados
};

export default UserProfile;
