import React from 'react';

const CartPage = ({ cart }) => {
  const { cart: cartItems, removeFromCart, clearCart } = cart;

  return React.createElement(
    'div',
    { className: 'cart-container' },
    React.createElement('h1', null, 'Carrinho de Compras'),
    cartItems.length === 0
      ? React.createElement('p', null, 'O carrinho está vazio.')
      : React.createElement(
          React.Fragment,
          null,
          React.createElement(
            'div',
            { className: 'cart-product-list' },
            cartItems.map((item) =>
              React.createElement('div', { key: item.id, className: 'cart-product-card' },
                React.createElement('h3', null, item.name),
                React.createElement('img', { src: item.image, alt: item.name }),
                React.createElement('p', null, `${item.price} € x ${item.quantity}`),
                React.createElement('div', { className: 'cart-selected-product' },
                  React.createElement('button', { onClick: () => removeFromCart(item.id) }, 'Remover')
                )
              )
            )
          ),
          React.createElement(
            'div',
            { className: 'cart-summary' },
            React.createElement('h3', null, 'Resumo do Pedido'),
            cartItems.map((item) =>
              React.createElement('div', { key: item.id, className: 'cart-selected-product' },
                React.createElement('span', null, `${item.name} (${item.quantity})`),
                React.createElement('span', null, `${item.price * item.quantity} €`)
              )
            ),
            React.createElement(
              'div',
              { className: 'cart-selected-product' },
              React.createElement('strong', null, 'Total:'),
              React.createElement('strong', null, `${cartItems.reduce((total, item) => total + item.price * item.quantity, 0)} €`)
            ),
            React.createElement('button', { onClick: clearCart }, 'Limpar Carrinho')
          )
        )
  );
};

export default CartPage;
