// Faq.js

import React, { useState } from 'react';
import './Faq.css';

const Faq = () => {
  const [activeIndex, setActiveIndex] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  const toggleAnswer = (index) => {
    setActiveIndex(activeIndex === index ? null : index);
  };

  const filteredQuestions = () => {
    return FAQ_DATA.filter((item) =>
      item.question.toLowerCase().includes(searchTerm.toLowerCase())
    );
  };

  return (
    <div className="faq-container">
      <h2 className="faq-title">Perguntas Frequentes</h2>
      <input
        type="text"
        placeholder="Em que posso ajudar?"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="faq-search"
      />
      <div className="faq-items">
        {filteredQuestions().map((item, index) => (
          <div className="faq-item" onClick={() => toggleAnswer(index)} key={index}>
            <h3 className="question">{item.question}</h3>
            {activeIndex === index && <p className="answer">{item.answer}</p>}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Faq;

const FAQ_DATA = [
  {
    question: 'Como faço para realizar um pedido?',
    answer:
      'Para realizar um pedido, basta navegar até a página de produtos, escolher o produto desejado e seguir as instruções de checkout.',
  },
  {
    question: 'Qual é o prazo de entrega dos produtos?',
    answer:
      'O prazo de entrega pode variar dependendo da localização e do método de envio escolhido. Geralmente, os produtos são entregues dentro de 5 a 7 dias úteis.',
  },
  // Adicione mais perguntas e respostas conforme necessário
];
