// Faq.js

import React, { useState } from 'react';
import './Faq.css';

const Faq = () => {
  const [faqs, setFaqs] = useState([
    {
      question: "Como faço para realizar um pedido?",
      answer: "Para realizar um pedido, basta navegar até a página de produtos, escolher o produto desejado e seguir as instruções de checkout."
    },
    {
      question: "Qual é o prazo de entrega dos produtos?",
      answer: "O prazo de entrega pode variar dependendo da localização e do método de envio escolhido. Geralmente, os produtos são entregues dentro de 5 a 7 dias úteis."
    },
    // Adicione mais perguntas e respostas conforme necessário
  ]);

  const toggleAnswer = (index) => {
    const newFaqs = [...faqs];
    newFaqs[index].isOpen = !newFaqs[index].isOpen;
    setFaqs(newFaqs);
  };

  return (
    <div className="faq-container">
      <h2 className="faq-title">Perguntas Frequentes</h2>
      <div className="faq-items">
        {faqs.map((faq, index) => (
          <div className="faq-item" key={index} onClick={() => toggleAnswer(index)}>
            <h3 className="question">{faq.question}</h3>
            {faq.isOpen && (
              <p className="answer">{faq.answer}</p>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Faq;
