import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './EditCampaign.css';

const EditCampaign = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [name, setName] = useState('');
  const [discount, setDiscount] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [isActive, setIsActive] = useState(false);

  useEffect(() => {
    fetch(`http://localhost:5000/api/campaigns/${id}`)
      .then(response => response.json())
      .then(data => {
        setName(data.name);
        setDiscount(data.discount);
        setStartDate(data.startDate);
        setEndDate(data.endDate);
        setIsActive(data.isActive);
      })
      .catch(error => console.error('Error fetching campaign:', error));
  }, [id]);

  const handleUpdateCampaign = (e) => {
    e.preventDefault();
    const updatedCampaign = {
      name,
      discount: parseFloat(discount),
      startDate,
      endDate,
      isActive,
    };

    fetch(`http://localhost:5000/api/campaigns/${id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedCampaign),
    })
      .then(response => {
        if (!response.ok) {
          return response.text().then(text => { throw new Error(text) });
        }
        navigate('/gerir-campanhas');
      })
      .catch(error => console.error('Error updating campaign:', error));
  };

  return (
    <div className="edit-container">
      <h2>Editar Campanha</h2>
      <form className="edit-form" onSubmit={handleUpdateCampaign}>
        <label>Nome:</label>
        <input type="text" value={name} onChange={(e) => setName(e.target.value)} required />
        <label>Desconto (%):</label>
        <input type="number" value={discount} onChange={(e) => setDiscount(e.target.value)} required />
        <label>Data de Início:</label>
        <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} required />
        <label>Data de Fim:</label>
        <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} required />
        <label>
          Ativa:
          <input type="checkbox" checked={isActive} onChange={(e) => setIsActive(e.target.checked)} />
        </label>
        <button type="submit" className="btn-submit-edit">Salvar Alterações</button>
      </form>
    </div>
  );
};

export default EditCampaign;
